                            var _rapportBorne = conf.getHorizonCorrec(rapport, _intervalleX);

                            hor = (moy - _atterissageMinVar * (1 - _rapportBorne)) / _rapportBorne;
                            //hor = (moy - atterissageMinVar * (1 - rapportBorne)) / rapportBorne;
