    /// <summary>
    /// Données d'une période (début fin, nb 
    /// </summary>
    class Period
    {
        public readonly DateTime debutFutur, finFutur;
        public readonly int nb; 
        public Period(DateTime debutFutur, DateTime finFutur, int nb)
        {
            this.debutFutur = debutFutur;
            this.finFutur = finFutur;
            this.nb = nb;
        }
        public double Moy()
        {
            var delta = DeltaTotal;
            double moy = delta / nb;
            return moy; 
        }

        public double Moy(double offset)
        {
            var delta = DeltaTotal- offset;
            if (delta < 1)
                delta = 1; 
            double moy = delta / nb;
            return moy;
        }

        public double DeltaTotal { get { return (finFutur - debutFutur).TotalDays; } }

    }

    class PeriodBuilder
    {
        public static double getweek(double days)
        {
            return 7 * System.Math.Floor(days / 7.0);
        }
        public static void buildWeek(List<double> list)
        {
            for (int i = 0; i < list.Count; i++)
            {
                list[i] = getweek(list[i]);
            }
        }
        public static Period getDensite(List<TrackSort> list, int index0, SortItem conf, double futur)
        {
            DateTime now = DateTime.Now;
            int count = list.Count;
            int nbRestant = 0;
            int nbFutur = 0;
            var current = list[index0];
            futur = getweek(futur);
            DateTime debutFutur = current.Pub;
            var finFutur = current.Pub.AddDays(futur);
            //  finFuturH = current.Pub.AddDays(futurh);
            if (finFutur > now)
            {
                var d = getweek((now - debutFutur).TotalDays);
                finFutur = debutFutur.AddDays(d);
            }


            DateTime l = DateTime.MinValue;
            for (int i = index0; i < count; i++)
            {
                var t = list[i].Pub;
                if (t < debutFutur)
                    continue;
                if (t.Date == l.Date && list[i].track.Duree.TotalMinute < conf.collisionDuree)
                    continue;
                l = t;
                nbRestant++;

                if (t < finFutur)
                    nbFutur++;
                else
                    break; 

            }
            if (nbFutur == 0)
                nbFutur = 1;
            return new Period(debutFutur, finFutur, nbFutur); 
        }
    }
}

