    [Serializable]
    public class TuningBase : ICloneable
    {
        public const string DIR_CONF = @"..\conf\";

        [NonSerialized]
        protected XmlElement parent;
        [NonSerialized]
        protected XmlElement child;

        public TuningBase()
            : this(null, null)
        {
        }

        public TuningBase(XmlElement node) : this(null, node) { }

        public TuningBase(XmlElement parent, XmlElement child)
        {
            this.parent = parent;
            this.child = child;
        }
        protected XmlWriter w;
        public virtual void save(XmlWriter w) { this.w = w; }
        protected void initWrite(string filename)
        {
            var settings = new XmlWriterSettings();
            settings.Indent = true;
            settings.Encoding = Encoding.UTF8;
            w = XmlWriter.Create(filename, settings);
        }

        protected void EndWrite()
        {
            w.Close();
        }

        protected void writeStart(string name)
        {
            w.WriteStartElement(name);
        }
        protected void writeEnd()
        {
            w.WriteEndElement();
        }

        private string getStrValue(object value)
        {
            string val = "";
            if (value != null)
            {
                if (value is bool || value is bool?)
                    val = (bool)value ? "true" : "false";
                else if (value is DateTime)
                {
                    var d = (DateTime)value;
                    val = d.ToString("dd/MM/yyyy HH:mm");
                    if (d.Second > 0)
                        val = d.ToString();
                }
                else
                    val = value.ToString();
            }
            return val;
        }
        protected void write(string name, object value)
        {
            string val = getStrValue(value);
            w.WriteAttributeString(name, val);
        }
        protected void writeInnerText(XmlElement root, string name, object value)
        {
            string val = getStrValue(value);
            var node = root.SelectSingleNode("./" + name);
            node.InnerText = val;
        }

        public void Write(string file)
        {

            var doc = new XmlDocument();
            doc.PreserveWhitespace = true;
            doc.Load(file);
            writeInnerText(doc.DocumentElement);
            doc.Save(file);
        }

        public virtual void writeInnerText(XmlElement root) { }



        /// <summary>
        /// Charge un double
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setd"></param>
        protected void buildd(string name, setd setd)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                setd(Convert.ToDouble(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                setd(Convert.ToDouble(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                setd(Convert.ToDouble(elt.InnerText));
            elt = child[name];
            if (elt != null)
                setd(Convert.ToDouble(elt.InnerText));
        }
        /// <summary>
        /// Charge un décimal
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setd"></param>
