                        deltaElastique2 = coeffLot * coeffElastique2sm;
                        //_nbElastique += coeffLot * coeffElastique1sm; // coeffElastique1* rapportmp;
                        //_nbElastique2 += coeffLot * coeffElastique2sm; // coeffElastique2r* rapportmp;
