        private static decimal getMedianne_(IList<Piece> list, bool main, decimal coeffMedianne)
        {
            var l = new List<decimal>();
            foreach (Piece p in list)
            {
                if (main)
                    l.Add(p.VirtualClassement);
                else
                    l.Add(p.VirtualClassement2);
            }

            return CalcMedianne.getMedianneDec(l, coeffMedianne);
        }


        private static decimal getMedianne_(IList<PieceExtended> list, decimal coeffMedianne)
        {
            index = 0;
            if (list == null)
                throw new ArgumentException("liste nulle");
            int count = list.Count;
            if (count == 0)
                throw new ArgumentException("liste vide");
            if (coeffMedianne < 0 || coeffMedianne > 1)
                throw new ArgumentException("coeffMedianne doit être compris entre 0 et 1, valeur:" + coeffMedianne);
            if (count == 1)
                return list[0].Value;
            if (coeffMedianne == 0)
                return list[0].Value;
            if (coeffMedianne == 1)
            {
                index = count - 1;
                return list[count - 1].Value;
            }

            var medianne = coeffMedianne * (count - 1);

            index = (int)medianne;
            int i1 = index + 1;
            var val0 = list[index].Value;
            var val1 = list[i1].Value;
            if (Math.Abs(medianne - Convert.ToDecimal(index)) < PRECISION)
                return val0;
            if (Math.Abs(medianne - i1) < PRECISION)
            {
                index = i1;
                return val1;
            }
            if (val1 < PRECISION)
            {
                index = i1;
                return val1;
            }
            var dx = medianne - index;


            // var ret = val0 + pente * dx;


            // moyenne avec calcul de log
            var log0 = Math.Log(val0 + EPSILON_LOG);
            var log1 = Math.Log(val1 + EPSILON_LOG);
            // pente = log1 - log0;
            var log = log0 + (log1 - log0) * dx;
            return Math.Exp(log) - EPSILON_LOG;


        }

    }
}

using pdb.util;
using System;

namespace pdb.gen.albums
{
    public class PieceExtended : IValue
    {
        public enum mode
        {
            main,
            min,
            combine
        }
        private Piece piece;
        private PieceSupInfo main;
        private PieceSupInfo min;
        private PieceSupInfo combine;
        private mode _mode;
        // private bool effectMain;


        public PieceExtended(Piece piece)
        {
            this.piece = piece;
            var cl = piece.RapportClassement;
            this.main = new PieceSupInfo(cl);
            this.min = new PieceSupInfo(cl);
        }

        public void Combine()
        {
            this.combine = new PieceSupInfo(min.Value);
            var coeffmain = main.getCoeff(piece.RapportClassement);
            if (coeffmain > 1)
                combine.setCoeff(coeffmain, min.Value);
        }

        public decimal VirtualClassement { get { return main.Value; } }
        public decimal VirtualClassement2 { get { return min.Value; } }
        public decimal Cumul { get { return combine.Value; } }

        public bool getCauseUp(bool? natureCause)
        {
            if (true == natureCause)
                return min.getCoeff(piece.RapportClassement) > 1;
            if (false == natureCause)
                return main.getCoeff(piece.RapportClassement) > 1;
            return min.getCoeff(piece.RapportClassement) > 1 || main.getCoeff(piece.RapportClassement) > 1;
        }

        public bool? getSimpleCause()
        {
            var cl = piece.RapportClassement;
            var coeff1 = main.getCoeff(cl);
            var coeff2 = min.getCoeff(cl);
            if (coeff2 > coeff1)
                return true;
            else if (coeff1 > coeff2)
                return true;
            return null;
        }

        public decimal CoeffVirtuel { get { return main.getCoeff(piece.RapportClassement); } }
        public decimal CoeffVirtuel2 { get { return min.getCoeff(piece.RapportClassement); } }
        public decimal getVirtual(bool main)
        {
            if (main)
                return this.main.Value;
            else
                return this.min.Value;
        }
        public Piece Piece { get { return piece; } }
        public bool Enabled { get { return piece.Enabled; } }

        public bool LastClassementRecent { get { return piece.LastClassementRecent; } }
        public decimal RapportClassement { get { return piece.RapportClassement; } }


        public void setCoeff(decimal coeff, bool main)
        {
            var value0 = piece.RapportClassement;
            if (main)
                this.main.setCoeff(coeff, value0);
            else
                this.min.setCoeff(coeff, value0);
        }

        public void setValue(decimal value, bool main)
        {
            if (main)
                this.main.Value = value;
            else
                this.min.Value = value;
        }

        public void finalyse()
        {
            this.piece.setClassementVirtuel(this.main.Value);
            this.piece.setClassementVirtuel2(this.min.Value);
        }

        public override string ToString()
        {
            return string.Format("{0} enabled={1} coeff1={2} coeff2={3} {4}", pdb.gen.Piece.getClassementEquiv(RapportClassement), Enabled, CoeffVirtuel, CoeffVirtuel2, Piece);
        }


        internal PieceSupInfo Current
        {
            get
            {
                switch (this._mode)
                {
                    case mode.main: return main;
                    case mode.min: return min;
                    case mode.combine: return combine;
                    default: throw new NotImplementedException();
                }

            }
        }

        public decimal Value
        {
            get
            {
                return Current.Value;
            }

            set
            {
                Current.Value = value;
            }
        }

        public mode Mode
        {
            get { return _mode; }
            set { _mode = value; }
        }
        //  public bool EffectMain { get { return effectMain; } set { effectMain = value; } }
    }

    class PieceSupInfo
    {


        //  public readonly decimal value0;
        private decimal value;

        public PieceSupInfo(decimal value)
        {
            this.value = value;
            //  this.value0 = value;
        }

        public decimal getCoeff(decimal value0)
        {
            if (value <= 0) return 1m; return value0 / value;
        }

        public void setCoeff(decimal coeff, decimal value0)
        {
            if (value0 > 0)
                this.value = value0 / coeff;

        }

        public decimal Value { get { return value; } set { this.value = value; } }
    }
}

using System;
using pdb.gen.medianne;
using pdb.util;
using System.Xml;
using pdb.gen.auto;

namespace pdb.gen.Tuning
{
    [Serializable]
    public class ConfMedianne : TuningBase
    {
        public medianneMode type;
        public decimal medianne = 0.5m;
        public bool quasi;

        public bool enabled;



        public bool removeFirst;
        public bool deleteFirst;
        public removeLast removeLast;

        public decimal expEnabled = 0m;

        public decimal zero = 0m;
        /// <summary>
        /// 
        /// </summary>
        public bool removeEnabled;
        /// <summary>
        /// cumule l'influence des deux parties main et min
        /// </summary>
        public bool combine;
        /// <summary>
        /// calcul simple des selectedMotif
        /// </summary>
        public bool simple;



        public modeMin cmpMin;

        private ConfMedianneVirt _main;
        private ConfMedianneVirt _min;

        public ConfMedianneVirt Main { get { return _main; } }
        public ConfMedianneVirt Min { get { return _min; } }

        public ConfMedianne()
        {
            _main = new ConfMedianneVirt();
            _min = new ConfMedianneVirt();

        }

        public void makeIdem()
        {
            _min = _main;
        }

        public void duplicateMainIf()
        {
            if (_min == _main)
                _min = new ConfMedianneVirt(_main);
        }

        public ConfMedianne(ConfMedianne o, bool raz)
        {
            type = o.type;
            medianne = o.medianne;
            quasi = o.quasi;

            enabled = o.enabled;



            removeFirst = o.removeFirst;
            deleteFirst = o.deleteFirst;
            removeLast = o.removeLast;

            expEnabled = o.expEnabled;

            zero = o.zero;
            removeEnabled = o.removeEnabled;



            cmpMin = o.cmpMin;

            _main = new ConfMedianneVirt(o._main, raz);
            _min = new ConfMedianneVirt(o._min, raz);

            this.simple = o.simple;
            this.combine = o.combine; 

        }

        public ConfMedianne(XmlElement parent, XmlElement child)
            : base(parent, child)
        {
            var strType = "";
            builds("type", v => strType = v);
            type = (medianneMode)Enum.Parse(typeof(medianneMode), strType, true);
            builddec("medianne", v => medianne = v);
            buildb("quasi", v => quasi = v);
            buildb("enabled", v => enabled = v);
            buildb("removeFirst", v => removeFirst = v);
            buildb("deleteFirst", v => deleteFirst = v);
            builddec("expEnabled", v => expEnabled = v);
            builddec("zero", v => zero = v);
            buildb("removeEnabled", v => removeEnabled = v);
            string _modeMin = "";
            builds("cmpMin", v => _modeMin = v);
            if (!string.IsNullOrEmpty(_modeMin))
                cmpMin = (modeMin)Enum.Parse(typeof(modeMin), _modeMin, true);

            string _removeLast = null;
            builds("removeLast", v => _removeLast = v);
            removeLast = (removeLast)Enum.Parse(typeof(removeLast), _removeLast, true);

            buildb("combine", v => combine = v);
            buildb("simple", v => simple = v);

            _main = null;
            _min = null;
            var subs = XMLTool.NodeLookUp(child, "virts");
            if (subs != null)
            {
                foreach (XmlNode _n in subs.ChildNodes)
                {
                    if (_n is XmlElement)
                    {
                        if (_main == null)
                        {
                            _main = new ConfMedianneVirt(child, _n as XmlElement);

                        }
                        else if (_min == null)
                        {
                            _min = new ConfMedianneVirt(child, _n as XmlElement);
                        }
                    }
                }
                if (_min == null && _main != null)
                    _min = new ConfMedianneVirt(_main);
            }
        }

        public override void save(XmlWriter w)
        {
            base.save(w);
            writeStart("cmp");
            write("type", type);
            write("medianne", medianne);
            write("quasi", quasi);
            write("enabled", enabled);
            write("removeFirst", removeFirst);
            write("deleteFirst", deleteFirst);
            write("expEnabled", expEnabled);
            write("zero", zero);
            write("removeEnabled", removeEnabled);
            write("cmpMin", cmpMin);
            write("removeLast", removeLast);
            write("combine", combine);
            write("simple", simple); 

            writeStart("virts");
            _main.save(w);
            _min.save(w);
            writeEnd();

            writeEnd();



        }


