using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace buroSimple
{
    class Program
    {
        static void Main(string[] args)
        {
            int countArgs = args.GetLength(0);
            double delay = 20.0;
            bool unique = false;
            bool sortBySize = false;
            bool uniqueName = false;
            bool reverse = false; 
            bool fullscreen = false; 
            for (int i = 0; i <countArgs; i++)
            {
                if (args[i] == "-t")
                {
                    if (i + 1 < countArgs)
                    {
                        delay = Convert.ToDouble(args[i + 1]);
                    }

                }
                else if (args[i] == "/u")
                    unique = true;
                else if (args[i] == "/s")
                    sortBySize = true;
                else if (args[i] == "/un")
                    uniqueName = true;
                else if (args[i] == "/r")
                    reverse = true; 
                 else if (args[i] == "/fs")
                    fullscreen = true; 
            }
            var wp = new Walpaper() { delay = delay, unique = unique, sortBySize = sortBySize, uniqueName = uniqueName,reverse=reverse, fullscreen= fullscreen }; 
            wp.go(); 
        }
    }
}

