        void go()
        {
            // Scan(@"H:\spacer.gif");
            //  Scan(@"C:\temp\wp.bmp");
            //Scan(@"H:\noir.png");
            //Scan(@"H:\blanc.png");
            //Scan(@"H:\bleu.png");
